import React, { useLayoutEffect, useState, useEffect } from "react";
import { Link } from "react-router-dom";

import Header from './Header'

function Categories() {

    const [categories, setCategories] = useState([])

    useLayoutEffect(() => {
        fetch("https://blog.goldko.com.br/wp-json/wp/v2/categoria?taxonomie").then(response => {
            response.json().then(response => {
                setCategories(response)
            })
        })
    },[]);

    const Order = [
        "cafés",
        "cafés gelados",
        "cafés veganos",
        "chocolate",
        "comidinhas",
        "água sem gás",
    ]

    return (
        <div>
            <Header />
            <ul className="wdt-categories">
                { categories.length > 0 ?
                    categories.map(item => {
                        return (
                            <li style={"order:" + item.acf.ordenacao}>
                                <Link to={"/category/" + item.id }>
                                    <img src={ item.acf.capa } />
                                    <h2>{ item.name }</h2>
                                </Link>
                            </li> 
                        )
                    })
                : null}
            </ul> 
        </div>
    )
}

export default Categories;
