import React, { useEffect, useLayoutEffect, useState }from 'react';
import { Link } from "react-router-dom";

import Header from './Header'
import Modal from './Modal';

function Detail({ match }) {

    const [product, setProduct] = useState([])
    const [modal, setModal] = useState(false)

    console.log(match.params.id)

    fetch("https://blog.goldko.com.br/wp-json/wp/v2/cardapio/" + match.params.id + "?_embed").then(response => {
        response.json().then(response => {
            console.log("resp >", response)
            setProduct(response)
            return
        })
    })

    useEffect(() => {
        console.log(product)
    }, [product])

    return(
        <div>
            <Header />
            <div className="fx">
                <Link className="back" to="/"> { "< voltar" } </Link>
                {/* <a className="blends" onClick={ ()=>setModal(true) }>Entenda nossos blends</a> */}
            </div>
            { console.log("p", product.id) }
            { product.length > 0 || product.id ? 
            <div className="detail">
                <img src={ product._embedded["wp:featuredmedia"][0].source_url } />
                <div className="content">
                    <h2>
                        <span>{ product.title.rendered }</span>
                        <div className="flag">
                            <img src="https://blog.goldko.com.br/wp-content/uploads/2021/04/flag-vegano.png" />
                        </div>
                    </h2>
                    <div dangerouslySetInnerHTML={{ __html: product.content.rendered.replace("<p>&nbsp;</p>", "") }} />
                    <span className="price">{product.acf.preco}</span>
                </div>

                <div className="full-box">
                    <h3>Blends exclusivos GoldKo<br />escolha o seu</h3>
                    { product._embedded["wp:term"][3].length > 0 ?
                        product._embedded["wp:term"][3].map(item => {
                            console.log(item)
                            return (
                                <div className="acompanhamento">
                                    <img src={item.acf.capa} />
                                    <h4>{ item.name } </h4>
                                </div>
                            )
                        })
                    : null}
                </div>

                <div className="full-box">
                    <h3>Os cafés incluem 1 acompanhamento, escolha o seu</h3>
                    { product._embedded["wp:term"][1].length > 0 ?
                        product._embedded["wp:term"][1].map(item => {

                            return (
                                <div className="acompanhamento">
                                    <img src={item.acf.capa} />
                                    <h4>{ item.name } </h4>
                                </div>
                            )
                        })
                    : null}
                </div>

                <div className="full-box">
                    <h3>Deixe seu café ainda mais cremoso, inclua acompanhamento extra por R$ 5,50</h3>
                    { product._embedded["wp:term"][2].length > 0 ?
                        product._embedded["wp:term"][2].map(item => {

                            return (
                                <div className="acompanhamento">
                                    <img src={item.acf.capa} />
                                    <h4>{ item.name } </h4>
                                </div>
                            )
                        })
                    : null}
                </div>


            </div>
            : null }
        </div>
    )
}

export default Detail;
