                        console.log(item)
                        return (
                            <li style={{ order: item.acf.ordenacao ? item.acf.ordenacao : null }}>
                                <Link to={"/category/" + item.id }>
                                    <img src={ item.acf.capa } alt={ item.name } />
                                    <h2>{ item.name }</h2>
                                </Link>
                            </li> 
                        )
                    })
                : null}
            </ul> 
        </div>
    )
}

export default Categories;
