import React, { useEffect, useLayoutEffect, useState }from 'react';
import { Link } from "react-router-dom";

import Header from './Header'
import Modal from './Modal';

function Detail({ match }) {

    const [product, setProduct] = useState([])
    const [modal, setModal] = useState(false)

    useLayoutEffect(() => {
        fetch("https://blog.goldko.com.br/wp-json/wp/v2/cardapio?categoria=" + match.params.id + "&_embed").then(response => {
            response.json().then(response => {
                setProduct(response)
                console.log(response)
            })
        })
    },[]);

    useEffect(() => {
        console.log("Modal", modal)
    }, [modal])

    return(
        <div>
            <Header />
            <div className="fx">
                <Link className="back" to="/"> { "< voltar" } </Link>
                <a className="blends" onClick={ ()=>setModal(true) }>Entenda nossos blends</a>
            </div>
            <div className={ modal ? "modal display-block" : "modal display-none"}>
                <section className="modal-main">
                    {children}
                    <button type="button" onClick={ ()=>setModal(false) }>
                    Close
                    </button>
                </section>
            </div>
            <ul className="detail-list">
                { product.length > 0 ?
                    product.map(item => {
                        console.log(item)
                        return (
                            <li style={{ 
                                backgroundImage: "url(" + item._embedded["wp:featuredmedia"][0].source_url + ")",
                                backgroundPosition: 'center',
                                backgroundSize: 'cover',
                                backgroundRepeat: 'no-repeat' }}>
                                <div className="text">
                                    <span>
                                        <h2>{ item.title.rendered }</h2>
                                        <div dangerouslySetInnerHTML={{ __html: item.content.rendered }} />
                                        <span className="price">R$ {item.acf.preco.replace(".", ",")}</span>
                                    </span>
                                </div>
                            </li> 
                        )
                    })
                : null}
            </ul>
        </div>
    )
}

export default Detail;
