import React, { useLayoutEffect, useState } from "react";
import { Link } from "react-router-dom";

import Header from './Header'

function Categories() {

    const [categories, setCategories] = useState([])

    useLayoutEffect(() => {
        fetch("https://blog.goldko.com.br/wp-json/wp/v2/categoria?taxonomie&_embed").then(response => {
            response.json().then(response => {
                setCategories(response)
            })
        })
    },[]);

    return (
        <div>
            <Header />
            <ul className="wdt-categories">
                { categories.length > 0 ?
                    categories.map(item => {
                        console.log(item)
                        return (
                            <li style={{ order: item.acf.ordenacao ? item.acf.ordenacao : null }}>
                                <Link to={"/category/" + item.id }>
                                    <img src={ item.acf.capa } alt={ item.name } />
                                    <h2>{ item.name }</h2>
                                </Link>
                            </li> 
                        )
                    })
                : null}
            </ul> 
        </div>
    )
}

export default Categories;
