import React, { useEffect, useLayoutEffect, useState }from 'react';
import { Link } from "react-router-dom";

import Header from './Header'
import Modal from './Modal';

function Detail({ match }) {

    const [product, setProduct] = useState([])
    const [modal, setModal] = useState(false)

    useLayoutEffect(() => {
        fetch("https://blog.goldko.com.br/wp-json/wp/v2/cardapio/" + match.params.id + "?_embed").then(response => {
            response.json().then(response => {
                setProduct(response)
                console.log(response)
            })
        })
    },[]);



    if(product) {
    return(
        <div>
            <Header />
            <div className="fx">
                <Link className="back" to="/"> { "< voltar" } </Link>
                {/* <a className="blends" onClick={ ()=>setModal(true) }>Entenda nossos blends</a> */}
            </div>
            <div className="detail">
            <img src={ product._embedded["wp:featuredmedia"][0].source_url } />
                <div className="content">

                    <h2>{ product.title.rendered }</h2>
                    <div dangerouslySetInnerHTML={{ __html: product.content.rendered.replace("<p>&nbsp;</p>", "") }} />
                    <span className="price">{product.acf.preco}</span>
                </div>




            </div>
        </div>
    )
    }
}

export default Detail;
