import React, { useEffect, useLayoutEffect, useState }from 'react';
import { Link } from "react-router-dom";

import Header from './Header'
import Modal from './Modal';

function Detail({ match }) {

    const [product, setProduct] = useState([])
    const [modal, setModal] = useState(false)

    useLayoutEffect(() => {
        fetch("https://blog.goldko.com.br/wp-json/wp/v2/cardapio/" + match.params.id + "?_embed").then(response => {
            response.json().then(response => {
                setProduct(response)
                console.log(response)
            })
        })
    },[]);

    console.log(product)

    if(product) {
    return(
        <div>
            <Header />
            <div className="fx">
                <Link className="back" to="/"> { "< voltar" } </Link>
                {/* <a className="blends" onClick={ ()=>setModal(true) }>Entenda nossos blends</a> */}
            </div>
            <ul className="detail">

                <div className="content">
                    <h2>{ product.title.rendered }</h2>
                    <div dangerouslySetInnerHTML={{ __html: product.content.rendered.replace("<p>&nbsp;</p>", "") }} />
                    <span className="price">{product.acf.preco}</span>
                </div>

                <div className="">
                    <h3>Blends exclusivos GoldKo, escolha o seu:</h3>
                    <div dangerouslySetInnerHTML={{ __html: product.acf.texto_do_blend.replace("<p>&nbsp;</p>", "") }} />
                </div>

                <h3>Os cafés incluem 1 acompanhamento, escolha o seu:</h3>
                { product._embedded["wp:term"][1].length > 0 ?
                    product._embedded["wp:term"][1].map(item => {
                        console.log(item)
                        return (
                            <div className="acompanhamento">
                                <img src={item.acf.capa} />
                                <h4>{ item.name } </h4>
                            </div>
                        )
                    })
                : null}

                <h3>Deixe seu café ainda mais cremoso, inclua acompanhamento extra por R$ 5,50</h3>
                { product._embedded["wp:term"][2].length > 0 ?
                    product._embedded["wp:term"][2].map(item => {
                        console.log(item)
                        return (
                            <div className="acompanhamento">
                                <img src={item.acf.capa} />
                                <h4>{ item.name } </h4>
                            </div>
                        )
                    })
                : null}
            </ul>
        </div>
    )
    }
}

export default Detail;
